resources.string:
function planetian(s) { return (s.endsWith("i") ? s : s + "i").toLowerCase(); }

function withArticle(s) { return startsWithVowel(s) ? "az " + s  : "a " + s; }
function startsWithVowel(s) { return /[aáeéiíoóöőuúüű]/.test(s.charAt(0).toLowerCase()); }
function withStartingArticle(s) { return capitalize(withArticle(s)); }
function capitalize(s) { return s.charAt(0).toUpperCase() + s.substring(1); }
